echo "from bloomberg:"
curl -s "https://www.bloomberg.com/markets/api/quote-page/$1%3ACUR" -H 'authority: www.bloomberg.com' -H 'upgrade-insecure-requests: 1' -H 'user-agent: Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/68.0.3440.106 Safari/537.36' -H 'accept: text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,image/apng,*/*;q=0.8' -H 'accept-encoding: gzip, deflate, br' -H 'accept-language: en-US,en;q=0.9,zh-TW;q=0.8,zh;q=0.7' --compressed | sed -e 's/\"price\"/\'$'\n\"price\"/g' | grep '^"price":[0-9]' | cut -d',' -f1 | cut -d':' -f2
